import React from 'react';
import './beats.css'

class BeatsContainer extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            rowCount: 1,
            rows: [],
        }
    }
    render() {
        this.renderRows()
        return React.createElement(
            'div',
            { className: "beats_container", id: "beats_container"},
            this.state.rows
        );
    }
    renderRows() {
        for (let i = 0; i < this.state.rowCount; i++){
            this.state.rows.push(<BeatRow row={i}/>)
        }
    }
}

class BeatRow extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            boxCount: 4,
            boxes: []
        };
    }
    render() {
        this.renderBoxes()
        return React.createElement(
            'div', { className: "beat_row", row: this.props.row }, this.state.boxes
        );
    }
    renderBoxes() {
        for (let i = 0; i < this.state.boxCount; i++) {
            this.state.boxes.push(<BeatBox box={i}/>)
        }
    }
}

class BeatBox extends React.Component {
    render() {
        const boxContent = `Box ${this.props.box}`;
        return React.createElement(
            'div', { className: "beat_box", beat: this.props.box }, boxContent
        );
    }
}

export default BeatsContainer;
