import React from 'react';
import ReactDOM from 'react-dom';
import './sounds.css';

class SoundsContainer extends React.Component {
    renderSoundContainer(count) {
        return (
            <div className="sounds_container">
                {this.renderSoundCount(1)}
                {this.renderSoundCard("Drum")}
            </div>
        );
    }
    renderSoundCard() {
        return <SoundCard />;
    }
    renderSoundCount() {
        return <SoundCard />;
    }
    renderSoundItem(name){
        return <SoundCard name={name}/>;
    }
    renderSoundItem(name){
        return <SoundCard name={name}/>;
    }
    render() {
        this.renderSoundContainer()
        this.renderNavContainer()
    }
}

class SoundCount extends React.Component {
    render() {
        return (
            <div className="sound_count" data-sound-count={this.props.count}>
                {this.props.count}
            </div>
        );
    }
}

class SoundCard extends React.Component {
    renderArrow(symbol, direction) {
        return (
            <div className="nav_arrow" data-direction={direction}>{symbol}</div>
        );
    }
    render() {
        return (
            <div className="sound_card" data-sound-name={this.props.name}> {this.props.name} </div>
        );
    }
}

export default SoundsContainer;
