import React from 'react';
import './sounds.css';

import appState from '../appState.js'

import NavContainer from '../nav/nav.js';

class SoundsContainer extends React.Component {
    render() {
        return React.createElement(
            'div',
            { className: "sounds_container" },
            <NavContainer key="nav_container" noteValue={this.props.noteValue} incrementNoteValue={this.props.incrementNoteValue} decrementNoteValue={this.props.decrementNoteValue}/>,
            <SoundCardContainer key="sound_card_container"/>
        );
    }
}

class SoundCardContainer extends React.Component {
    render() {
        const rows = Object.keys(appState.getSoundRows).map((row, index) => {
            return <SoundRow cards={appState.getSoundRows[row]} key={row} row={row}/>
        })
        return React.createElement(
            'div',
            { className: "sound_card_container" },
            rows
        );
    }
}

class SoundRow extends React.Component {
    render() {
        const cards = this.props.cards.map((card) => {
            return <SoundCard key={card} name={card}/>
        })
        return React.createElement(
            'div',
            { className: "sound_row" },
            cards
        );
    }
}

class SoundCard extends React.Component {
    render() {
        return (
            <div className="sound_card" name={this.props.name}>
                {this.props.name}
            </div>
        );
    }
}

export default SoundsContainer;
