import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class BeatBox extends React.Component {
    render() {
        return (
            <div className="beat_box" box-id={this.props.id}>Box {this.props.id}</div>
        );
    }
}

class BeatsContainer extends React.Component {
    renderBox(id) {
        return <BeatBox id={id} />;
    }

    render() {
        return (
            <div className="beats_container">
                <div className="beat_row" row-id="1">
                    {this.renderBox(0)}
                    {this.renderBox(1)}
                    {this.renderBox(2)}
                    {this.renderBox(3)}
                </div>
            </div>
        );
    }
}

class SoundItem extends React.Component {
    render() {
        return (
            <div className="sound_item" data-sound-name={this.props.name} data-sound-count={this.props.count}>
            {this.props.name}
            </div>
        );
    }
}

class SoundsContainer extends React.Component {
    renderSoundItem(name, count){
        return <SoundItem name={name} count={count} />
    }
    render() {
        return (
            <div className="sounds_container">
                <div className="sounds_count" data-count="1">
                    {this.renderSoundItem("Drum", 1)}
                    {this.renderSoundItem("Shekera", 1)}
                    {this.renderSoundItem("Gahna", 1)}
                </div>
            </div>
        );
    }
}

class AppContainer extends React.Component {
    renderBeats() {
        return <BeatsContainer/>
    }
    renderSounds() {
        return <SoundsContainer/>
    }
    render() {
        return (
            <div className="djim-beats-container">
                {this.renderBeats()}
                {this.renderSounds()}
            </div>
        );
    }
}
ReactDOM.render(
    <AppContainer/>,
    document.getElementById('root')
);
