import React from 'react';
import './sounds.css';

import NavContainer from '../nav/nav.js';

class SoundsContainer extends React.Component {
    constructor(props) {
        const noteValues = {
            1: ["Drum", "Shekera", "Gahna"]
        };
        this.state = {
            currentNoteValue: 1,
            cards: [],
            noteValues
        };
    }
    render() {
        return React.createElement(
            'div', { className: "sounds_container" },
            <SoundCount noteValue={this.state.currentNoteValue}/>,
        );
    }
    renderCards() {
        const cards = this.state.noteValues[this.state.currentNoteValue];
        console.log(cards);
        for (let i = 0; i < cards.length; i++) {
            this.state.cards.push(<SoundCard name={cards[i]}/>)
        }
    }
}

class SoundCount extends React.Component {
    render() {
        return React.createElement(
            'div',
            { className: "sound_count", value: this.props.noteValue },
            this.props.noteValue
        );
    }
}

class SoundCard extends React.Component {
    render() {
        return React.createElement(
            'div',
            { className: "sound_card", name: this.props.name },
            this.props.name
        );
    }
}

export default SoundsContainer;
