import React from 'react'


class BeatsState extends React.Component{
    state = {
        beatRowCount: 1,
        soundRowCount: 1,
        soundCardCount: 0,
        noteValue: 1
    }
    decrementNoteValue() {
        this.state.noteValue -= 1
    }
}

export default BeatsState;
