import React from 'react';

import './beats.css'

class BeatsContainer extends React.Component {
    render() {
        const rows = this.renderRows()
        return React.createElement(
            'div',
            { className: "beats_container" },
            rows
        );
    }
    renderRows() {
        const rows = []
        for (let row = 1; row <= this.props.measures; row += 1) {
            rows.push(<BeatRow key={row} row={row} bpm={this.props.bpm}/>)
        }
        return rows
    }
}

class BeatRow extends React.Component {
    render() {
        // const boxes = this.renderBoxes()
        return (
            <div class="beat_row" row={this.props.row}>
                {this.renderBoxes()}
            </div>
        );
    }
    renderBoxes() {
        const boxes = []
        for (let box = 1; box <= this.props.bpm; box += 1) {
            boxes.push(<BeatBox key={box} box={box}/>)
        }
        return boxes
    }
}

class BeatBox extends React.Component {
    render() {
        return (
            <div class="beat_box" box={this.props.box}>
                {this.props.box}
            </div>
        );
    }
}

export default BeatsContainer;
