import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class BeatBox extends React.Component {
    render() {
        return (
            <div className="beat_box" box-id={this.props.id}>Box {this.props.id}</div>
        )
    }
}

class BeatsContainer extends React.Component {
    renderBox(id) {
        return <BeatBox id={id} />;
    }

    render() {
        return (
            <div className="beats_container">
                <div className="row" row-id="1">
                    {this.renderBox(0)}
                    {this.renderBox(1)}
                    {this.renderBox(2)}
                    {this.renderBox(3)}
                </div>
            </div>
        );
    }
}

class SoundItem extends React.Component {
    render() {
        return (
            <div className="sound_box" box-id={this.props.name}>Box {this.props.id}</div>
        )
    }
}
class SoundsContainer extends React.Component {
    renderSoundItem(name){
        return <SoundItem name={name} />
    }
    render() {
        return (
            <div className="sounds_container">
                this.renderSoundItem("Drum", 1);
                this.renderSoundItem("Shekera", 1);
                this.renderSoundItem("Gahna", 1);
                <div className="sound-item" data-sound-name="Drum" data-sound-count="1"></div>
                <div className="sound-item" data-sound-name="Shekera" data-sound-count="1"></div>
                <div className="sound-item" data-sound-name="Gahna" data-sound-count="1"></div>
            </div>
        );
    }
}

ReactDOM.render(
    <BeatsContainer/>,
    document.getElementById('root')
);
