import React from 'react';
import ReactDOM from 'react-dom';
import './sounds.css';

class SoundsContainer extends React.Component {
    renderSoundCount(count) {
        return <SoundCount count={count}/>;
    }
    renderSoundItem(name){
        return <SoundCard name={name}/>;
    }
    renderArrow(symbol, direction) {
        return (
            <div className="nav_arrow" data-direction={direction}>{symbol}</div>
        );
    }
    render() {
        return (
            <div className="sounds_container">
                {this.renderSoundCount(1)}
                {this.renderArrow("<", "prev")}
                <div className="sound_card" data-sound-count="1">
                    {this.renderSoundItem("Drum")}
                    {this.renderSoundItem("Shekera")}
                    {this.renderSoundItem("Gahna")}
                </div>
                {this.renderArrow(">", "next")}
            </div>
        );
    }
}

class SoundNav extends React.Component {
    render() {
        return (
            <div className="sound_nav">
                {this.renderCurrent(this.props.current)}
            </div>
        );
    }
}

class SoundCount extends React.Component {
    render() {
        return (
            <div className="sound_count" data-sound-count={this.props.count}>
                {this.props.count}
            </div>
        );
    }
}

class SoundCard extends React.Component {
    render() {
        return (
            <div className="sound_card" data-sound-name={this.props.name}>
                {this.props.name}
            </div>
        );
    }
}

export default SoundsContainer;
