import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class BeatBox extends React.Component {
    render() {
        return (
            <div className="beat_box" box-id={this.props.id}>Box {this.props.id}</div>
        );
    }
}

class BeatsContainer extends React.Component {
    renderBox(id) {
        return <BeatBox id={id} />;
    }

    render() {
        return (
            <div className="beats_container">
                <div className="beat_row" row-id="1">
                    {this.renderBox(0)}
                    {this.renderBox(1)}
                    {this.renderBox(2)}
                    {this.renderBox(3)}
                </div>
                <div className="beat_row" row-id="2">
                    {this.renderBox(4)}
                    {this.renderBox(5)}
                    {this.renderBox(6)}
                    {this.renderBox(7)}
                </div>
            </div>
        );
    }
}

class SoundBox extends React.Component {
    render() {
        return (
            <div className="sound_box" data-sound-name={this.props.name}>
                {this.props.name}
            </div>
        );
    }
}

class SoundCount extends React.Component {
    render() {
        return (
            <div className="sound_count" data-sound-count={this.props.count}>
                Value: {this.props.count}
            </div>
        );
    }
}

class SoundsContainer extends React.Component {
    renderSoundCount(count) {
        return <SoundCount count={count}/>;
    }
    renderSoundItem(name){
        return <SoundBox name={name}/>;
    }
    render() {
        return (
            <div className="sounds_container">
                {this.renderSoundCount(1)}
                {this.renderSoundItem("Drum")}
                {this.renderSoundItem("Shekera")}
                {this.renderSoundItem("Gahna")}
            </div>
        );
    }
}

class AppContainer extends React.Component {
    renderBeats() {
        return <BeatsContainer/>
    }
    renderSounds() {
        return <SoundsContainer/>
    }
    render() {
        return (
            <div className="djim-beats-container">
                {this.renderBeats()}
                {this.renderSounds()}
            </div>
        );
    }
}
ReactDOM.render(
    <AppContainer/>,
    document.getElementById('root')
);
