import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import BeatsContainer from './beats.js';

class SoundBox extends React.Component {
    render() {
        return (
            <div className="sound_box" data-sound-name={this.props.name}>
                {this.props.name}
            </div>
        );
    }
}

class SoundCount extends React.Component {
    render() {
        return (
            <div className="sound_count" data-sound-count={this.props.count}>
                {this.props.count}
            </div>
        );
    }
}

class SoundsContainer extends React.Component {
    renderSoundCount(count) {
        return <SoundCount count={count}/>;
    }
    renderSoundItem(name){
        return <SoundBox name={name}/>;
    }
    render() {
        return (
            <div className="sounds_container">
                {this.renderSoundCount(1)}
                {this.renderSoundItem("Drum")}
                {this.renderSoundItem("Shekera")}
                {this.renderSoundItem("Gahna")}
            </div>
        );
    }
}

class AppContainer extends React.Component {
    renderBeats() {
        return <BeatsContainer/>
    }
    renderSounds() {
        return <SoundsContainer/>
    }
    render() {
        return (
            <div className="djim-beats-container">
                {this.renderBeats()}
                {this.renderSounds()}
            </div>
        );
    }
}
ReactDOM.render(
    <AppContainer/>,
    document.getElementById('root')
);
