import React from 'react';
import ReactDOM from 'react-dom';
import './beats.css'

class BeatBox extends React.Component {
    render() {
        return React.createElement(
            'div', { className: "beat_box", id: this.props.id }, "Box", this.props.id
        );
    }
}

class BeatRow extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            boxCount: 4,
            rows: [],
            boxes: []
        };
    }

    renderMeasure(boxCount) {
        for(let i = 0; i < boxCount; i++) {
            console.log(i);
        }
    }
    render() {
        return React.createElement(
            'div', { className: "beat_row" }, this.renderMeasure(4)
        );
    }
}

class BeatsContainer extends React.Component {
    constructor(props) {
        super(props)
        this.rowCount = 1;
    }
    render() {
        return React.createElement(
            'div', { className: "beats_container" }, <BeatRow rowCount={this.rowCount}/>
        );
    }
}

export default BeatsContainer;
