import React from 'react';
import ReactDOM from 'react-dom';

import BeatsContainer from './beats/beats.js';
import SoundsContainer from './sounds/sounds.js';
import TitleContainer from './title/title.js';

import './index.css';


class AppContainer extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            measureCount: 1,
            beatsPerMeasure: 4,
            soundRowCount: 1,
            soundCardCount: 0,
            maxCards: 6,
            noteValue: 1,
            maxValue: 4,
            maxRows: 4,
            title: "Tue tue",
            subtitle: "From Ghana",
            noteValues: {
                1: ["drum", "shekere", "Ghana"],
                2: ["Tubano"]
            },
        }

        this.decrementNoteValue.bind(this)
        this.incrementNoteValue.bind(this)
        this.decrementBeatRowCount.bind(this)
        this.incrementBeatRowCount.bind(this)
        this.decrementSoundCardCount.bind(this)
        this.incrementSoundCardCount.bind(this)
        this.decrementSoundRowCount.bind(this)
        this.incrementSoundRowCount.bind(this)

        // this.decrementNoteValue = () => { this.decrementNoteValue() }
        // this.incrementNoteValue = () => { this.incrementNoteValue() }
        // this.decrementBeatRowCount = () => { this.decrementBeatRowCount() }
        // this.incrementBeatRowCount = () => { this.incrementBeatRowCount() }
        // this.decrementSoundCardCount = () => { this.decrementSoundCardCount() }
        // this.incrementSoundCardCount = () => { this.incrementSoundCardCount() }
        // this.decrementSoundRowCount = () => { this.decrementSoundRowCount() }
        // this.incrementSoundRowCount = () => { this.incrementSoundRowCount() }

    }
    getSoundRows(){
        const soundRows = {}
        const cards = this.getSoundCards()
        cards.forEach((card, index) => {
            const rowNum = Math.ceil((index + 1) / this.state.maxCards)
            if (!soundRows[rowNum]) {
                soundRows[rowNum] = []
            }
            soundRows[rowNum].push(card)
        })
        return soundRows
    }
    getSoundCards(){
        return this.state.noteValues[this.state.noteValue]
    }
    decrementNoteValue() {
        console.log(this.state.noteValue)
        if (this.state.noteValue === 1) {
            this.setState({ noteValue: this.state.maxValue })
        } else {
            this.setState({ noteValue: this.state.noteValue - 1 })
        }
        console.log(this.state.noteValue)
    }
    incrementNoteValue() {
        console.log(this.state.noteValue)
        if (this.state.noteValue === this.state.maxValue) {
            this.setState({ noteValue: 1 })
        } else {
            this.setState({ noteValue: this.state.noteValue + 1 })
        }
        console.log(this.state.noteValue)
    }
    decrementBeatRowCount() {
        this.setState({ beatRowCount: this.beatRowCount - 1 })
    }
    incrementBeatRowCount() {
        this.setState({ beatRowCount: this.beatRowCount + 1 })
    }
    decrementSoundCardCount() {
        this.setState({ soundCardCount: this.soundCardCount - 1 })
    }
    incrementSoundCardCount() {
        this.setState({ soundCardCount: this.soundCardCount + 1 })
    }
    decrementSoundRowCount() {
        this.setState({ soundRowCount: this.soundRowCount - 1 })
    }
    incrementSoundRowCount() {
        this.setState({ soundRowCount: this.soundRowCount + 1 })
    }
    render() {
        return React.createElement(
            'div',
            { className: "djim-beats-container" },
            <TitleContainer key="title_container" title={this.state.title} subtitle={this.state.subtitle}/>,
            <BeatsContainer key="beats_container" measures={this.state.measureCount} bpm={this.state.beatsPerMeasure}/>,
            <SoundsContainer key="sounds_container" noteValue={this.state.noteValue} incrementNoteValue={this.incrementNoteValue} decrementNoteValue={this.decrementNoteValue}/>
        );
    }
}

ReactDOM.render(
    <AppContainer/>,
    document.getElementById('root')
);
