import React from 'react';

import './nav.css';


class NavContainer extends React.Component {
    constructor(props){
        super(props)
    }
    state = {
        noteValue: 1
    }
    decrementNum = () => function () {
        const num = (this.state.num - 1)
        this.setState({
            num: this.state.noteValue - 1
        })
        if (this.state.num < 0) {
            this.setState({ num })
        }
    }
    incrementNum = () => function () {
        console.log(this.state.num)
        this.setState({
            num: this.state.noteValue + 1
        })
    }
    render() {
        return React.createElement(
            'div',
            { className: "nav_container" },
            <NavButton id="dec" handleClick={this.decrementNum} direction="<"/>,
            <NavNumber key="note_value" noteValue={this.state.noteValue}/>,
            <NavButton id="inc" handleClick={this.incrementNum} direction=">"/>
        );
    }
}

class NavNumber extends React.Component {
    render() {
        return React.createElement(
            "div",
            { className: "nav_number" },
            this.props.noteValue
        );
    }
}

class NavButton extends React.Component {
    render() {
        return React.createElement(
            'button', {
                className: "nav_button",
                id: this.props.id,
                onClick: this.props.handleClick
            }, this.props.direction
        );
    }
}

export default NavContainer;
