import React from 'react';

import './nav.css';


class NavContainer extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            noteValue: this.props.noteValue,
            maxValue: 4
        }
        this.state.handleDecrement = () => {
            if (this.state.noteValue === 1) {
                this.setState({ noteValue: this.state.maxValue })
            } else {
                this.setState({ noteValue: this.state.noteValue - 1 })
            }
        }
        this.state.handleIncrement = () => {
            if (this.state.noteValue === this.state.maxValue) {
                this.setState({ noteValue: 1 })
            } else {
                this.setState({ noteValue: this.state.noteValue + 1 })
            }
        }
    }
    render() {
        return React.createElement(
            'div',
            { className: "nav_container" },
            <NavButton id="dec" handleClick={ this.state.handleDecrement } direction="<"/>,
            <NavNumber key="note_value" noteValue={this.state.noteValue}/>,
            <NavButton id="inc" handleClick={this.state.handleIncrement} direction=">"/>
        );
    }
}

class NavNumber extends React.Component {
    render() {
        return React.createElement(
            "div",
            { className: "nav_number" },
            this.props.noteValue
        );
    }
}

class NavButton extends React.Component {
    render() {
        return React.createElement(
            'button', {
                className: "nav_button",
                id: this.props.id,
                onClick: this.props.handleClick
            }, this.props.direction
        );
    }
}

export default NavContainer;
