import React from 'react';
import './sounds.css';

import NavContainer from '../nav/nav.js';

class SoundsContainer extends React.Component {
    constructor(props) {
        const noteValues = {
            1: ["Drum", "Shekera", "Gahna"]
        }
        this.state = {
            currentNoteValue: 1,
            soundCards: [],
            noteValues
        }
    }
    render() {
        this.renderSoundCards()
        return React.createElement(
            'div', { className: "sounds_container" }, null
        );
    }
    renderSoundCards() {
        this.setState({soundCards: []});
        const cardCount = this.state.noteValues[this.state.currentNoteValue].length;
        for (let i = 0; i < cardCount; i++) {
            return <SoundCard />;
        }
    }
    renderSoundCount() {
        return <SoundCard />;
    }
    renderSoundItem(name){
        return <SoundCard name={name}/>;
    }
    renderSoundItem(name){
        return <SoundCard name={name}/>;
    }
}

class SoundCount extends React.Component {
    render() {
        return (
            <div className="sound_count" data-sound-count={this.props.count}>
                {this.props.count}
            </div>
        );
    }
}

class SoundCard extends React.Component {
    renderArrow(symbol, direction) {
        return (
            <div className="nav_arrow" data-direction={direction}>{symbol}</div>
        );
    }
    render() {
        return (
            <div className="sound_card" data-sound-name={this.props.name}> {this.props.name} </div>
        );
    }
}

export default SoundsContainer;
