import React from 'react';
import './sounds.css';

import NavContainer from '../nav/nav.js';

class SoundsContainer extends React.Component {
    constructor(props) {
        const noteValues = {
            1: ["Drum", "Shekera", "Gahna"]
        }
        this.state = {
            currentNoteValue: 1,
            soundCards: [],
            noteValues
        }
    }
    render() {
        this.renderSoundCards()
        return React.createElement(
            'div', { className: "sounds_container" },
            <SoundCount noteValue={this.state.currentNoteValue}/>
            // this.state.soundCards
        );
    }
    renderSoundCards() {
        this.setState("soundCards", []);
        const cards = this.state.noteValues[this.state.currentNoteValue].length;
        for (let i = 0; i < cards.length; i++) {
            this.state.soundCards.push(<SoundCard />)
        }
    }
}

class SoundCount extends React.Component {
    render() {
        return React.createElement(
            'div', { className: "sound_count", value: this.props.noteValue },
            this.props.noteValue
        );
    }
}

class SoundCard extends React.Component {
    render() {
        return (
            <div className="sound_card" data-sound-name={this.props.name}> {this.props.name} </div>
        );
    }
}

export default SoundsContainer;
