import React from 'react';
import { FaPlusSquare, FaMinusSquare } from 'react-icons/fa';

import './beats.css'

class BeatsContainer extends React.Component {
    render() {
        return (
            <div className="beats_container">
                {this.renderRows()}
            </div>
        );
    }
    renderRows() {
        const rows = []
        for (let row = 1; row <= this.props.measures; row += 1) {
            rows.push(<BeatRow key={row} row={row} bpm={this.props.bpm} decrementMeasureCount={this.props.decrementMeasureCount}/>)
        }
        return rows
    }
}

class BeatRow extends React.Component {
    render() {
        return ((
            <div className="beat_row" key={this.props.row} row={this.props.row}>
                <FaMinusSquare key="minus_beat_row" className="minus_row" onClick={this.props.decrementMeasureCount}/>
                {this.renderBoxes()}
            </div>
        )
        );
    }
    renderBoxes() {
        const boxes = []
        for (let box = 1; box <= this.props.bpm; box += 1) {
            boxes.push(<BeatBox key={box} box={box}/>)
        }
        return boxes
    }
}

class BeatBox extends React.Component {
    render() {
        return (
            <div className="beat_box" box={this.props.box}>
                {this.props.box}
            </div>
        );
    }
}

export default BeatsContainer;
