import React from 'react';
import ReactDOM from 'react-dom';
import './sounds.css';

class SoundsContainer extends React.Component {
    renderSoundCount(count) {
        return <SoundCount count={count}/>;
    }
    renderSoundItem(name){
        return <SoundBox name={name}/>;
    }
    renderSoundNav(){
        return <SoundNav />;
    }
    render() {
        return (
            <div className="sounds_container">
                {this.renderSoundNav()}
                <div className="sound_box" data-sound-count="1">
                    {this.renderSoundCount(1)}
                    {this.renderSoundItem("Drum")}
                    {this.renderSoundItem("Shekera")}
                    {this.renderSoundItem("Gahna")}
                </div>
                <div className="sound_box" data-sound-count="2">
                    {this.renderSoundCount(2)}
                    {this.renderSoundItem("SaltSlurper")}
                    {this.renderSoundItem("MarbleFondler")}
                    {this.renderSoundItem("DoorScratcher")}
                </div>
            </div>
        );
    }
}

class SoundNav extends React.Component {
    renderArrow(symbol, direction) {
        return (
            <div className="nav_arrow" data-direction={direction}>{direction}</div>
        );
    }
    render() {
        return (
            <div className="sound_nav">
                {this.renderArrow("<")}
                {this.renderArrow(">")}
            </div>
        );
    }
}

class SoundCount extends React.Component {
    render() {
        return (
            <div className="sound_count" data-sound-count={this.props.count}>
                {this.props.count}
            </div>
        );
    }
}

class SoundBox extends React.Component {
    render() {
        return (
            <div className="sound_box" data-sound-name={this.props.name}>
                {this.props.name}
            </div>
        );
    }
}

export default SoundsContainer;
