import React from 'react';

import './beats.css'

class BeatsContainer extends React.Component {
    render() {
        return React.createElement(
            'div',
            { className: "beats_container" },
            this.renderRows()
        );
    }
    renderRows() {
        for (let row = 1; row <= this.props.measures; row += 1) {
            return <BeatRow key={row} row={row} bpm={this.props.bpm}/>
        }
    }
}

class BeatRow extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            boxCount: 4,
            boxes: []
        };
    }
    render() {
        this.renderBoxes()
        return React.createElement(
            'div',
            { className: "beat_row", row: this.props.row },
            this.state.boxes
        );
    }
    renderBoxes() {
        for (let box = 1; box < this.props.bpm ; box += 1) {
            return <BeatBox key={box} box={box}/>
        }
        let boxNumber = 0
        for (let i = 0; i < this.state.boxCount; i += 1) {
            boxNumber += 1
            this.state.boxes.push(
                <BeatBox key={boxNumber} box={boxNumber}/>
            )
        }
    }
}

class BeatBox extends React.Component {
    render() {
        return React.createElement(
            'div',
            { className: "beat_box", box: this.props.box },
            this.props.box
        );
    }
}

export default BeatsContainer;
