import React from 'react';
import './sounds.css';

import NavContainer from '../nav/nav.js';

class SoundsContainer extends React.Component {
    render() {
        return React.createElement(
            'div',
            { className: "sounds_container" },
            <NavContainer key="nav_container"/>,
            <SoundCardContainer key={"sound_card_container"}/>
        );
    }
}

class SoundCardContainer extends React.Component {
    constructor(props) {
        super(props)
        const noteValues = {
            1: ["drum", "shekere", "Ghana"],
            2: ["Tubano"]
        }
        this.state = {
            cards: [],
            rowsCount: 1,
            noteValue: 1,
            noteValues
        }
    }
    render() {
        // this.renderCards()
        return React.createElement(
            'div',
            { className: "sound_card_container" },
            this.state.cards
        );
    }
    // renderCards() {
    //     const cards = this.state.noteValues[this.state.noteValue]
    //     for (let i = 0; i < cards.length; i += 1) {
    //         this.state.cards.push(
    //             <SoundCard key={cards[i]} name={cards[i]}/>
    //         )
    //     }
    // }
}

class SoundRow extends React.Component {
    render() {
        return React.createElement(
            'div',
            { className: "sound_card", name: this.props.name },
            this.props.name
        );
    }
}

class SoundCard extends React.Component {
    render() {
        return React.createElement(
            'div',
            { className: "sound_card", name: this.props.name },
            this.props.name
        );
    }
}

export default SoundsContainer;
