import React from 'react';
import './sounds.css';

import { increment } from '../state.js'
import NavContainer from '../nav/nav.js';

class SoundsContainer extends React.Component {
    constructor(props) {
        super(props)
        this.state.handleDecrement = () => {
            if (this.state.noteValue === 1) {
                this.setState({ noteValue: this.state.maxValue })
            } else {
                this.setState({ noteValue: this.state.noteValue - 1 })
            }
        }
        this.state.handleIncrement = () => {
            if (this.state.noteValue === this.state.maxValue) {
                this.setState({ noteValue: 1 })
            } else {
                this.setState({ noteValue: this.state.noteValue + 1 })
            }
        }
        console.log(this.props)
    }
    render() {
        const cards = this.props.noteValues[this.state.noteValue]
        return React.createElement(
            'div',
            { className: "sounds_container" },
            <NavContainer key="nav_container"
                handleDecrement={this.state.handleDecrement}
                handleIncrement={this.state.handleIncrement}
                noteValue={this.state.noteValue}/>,
            <SoundCardContainer key="sound_card_container" cards={cards}/>
        );
    }
}

class SoundCardContainer extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            rows: [],
            maxCards: 4
        }
    }
    render() {
        this.renderRows()
        return React.createElement(
            'div',
            { className: "sound_card_container" },
            this.state.rows
        );
    }
    renderRows() {
        console.log(this.props)
        const rowCount = Math.ceil(this.props.cards.length / this.state.maxCards)
        let rowNumber = 0
        for (let i = 0; i < rowCount; i += 1) {
            rowNumber += 1
            this.state.rows.push(
                <SoundRow cards={this.props.cards} key={rowNumber} row={rowNumber} />
            )
        }
    }
}

class SoundRow extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            cards: []
        }
        console.log(this.state)
    }
    render() {
        this.renderCards()
        return React.createElement(
            'div',
            { className: "sound_row" },
            this.state.cards
        );
    }
    renderCards() {
        for (let i = 0; i < this.props.cards.length; i += 1) {
            this.state.cards.push(
                <SoundCard key={this.props.cards[i]} name={this.props.cards[i]}/>
            )
        }
    }
}

class SoundCard extends React.Component {
    render() {
        return React.createElement(
            'div',
            { className: "sound_card", name: this.props.name },
            this.props.name
        );
    }
}

export default SoundsContainer;
