import React from 'react'

class AppState extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            beatRowCount: 1,
            soundRowCount: 1,
            soundCardCount: 0,
            noteValue: 1,
            maxValue: 4,
            noteValues: {
                1: ["drum", "shekere", "Ghana"],
                2: ["Tubano"]
            },
            decrementNoteValue: this.decrementNoteValue(),
            incrementNoteValue: this.incrementNoteValue(),
            decrementBeatRowCount: this.decrementBeatRowCount(),
            incrementNoteValue: this.incrementBeatRowCount(),
            decrementSoundCardCount: this.decrementSoundCardCount(),
            incrementSoundCardCount: this.incrementSoundCardCount(),
            decrementSoundRowCount: this.decrementSoundRowCount(),
            incrementSoundRowCount: this.incrementSoundRowCount()
        }
    }
    decrementNoteValue() {
        if (this.state.noteValue === 1) {
            this.setState({ noteValue: this.state.maxValue })
        } else {
            this.setState({ noteValue: this.state.noteValue - 1 })
        }
    }
    incrementNoteValue() {
        if (this.state.noteValue === this.state.maxValue) {
            this.setState({ noteValue: 1 })
        } else {
            this.setState({ noteValue: this.state.noteValue + 1 })
        }
    }
    decrementBeatRowCount() {
        this.setState({ beatRowCount: this.beatRowCount - 1 })
    }
    incrementBeatRowCount() {
        this.setState({ beatRowCount: this.beatRowCount + 1 })
    }
    decrementSoundCardCount() {
        this.setState({ beatRowCount: this.beatRowCount - 1 })
    }
    incrementSoundCardCount() {
        this.setState({ beatRowCount: this.beatRowCount + 1 })
    }
}

const appState = new AppState()
export default appState;
